css:
.buy-button, .product__buy {
  background-color: #25D366; /* Verde WhatsApp */
  color: #ffffff;
  border: none;
  padding: 12px 24px;
  font-size: 16px;
  font-weight: bold;
  border-radius: 8px;
  cursor: pointer;
  text-transform: uppercase;
  transition: background-color 0.3s ease;
}

.buy-button:hover, .product__buy:hover {
  background-color: #1DA851; /* Verde um pouco mais escuro no hover */
}
